'use strict';const mongoose=require("mongoose"),validator=require("validator"),userSchema=new mongoose.Schema({name:{required:!0,type:String},nickname:{required:!0,type:String},email:{type:String,required:[!0,"Please provide your email"],unique:!0,lowercase:!0,validate:[validator.isEmail,"Please provide a valid email"]},password:{required:!0,type:String},favorites:{required:!0,type:[String]}});module.exports=mongoose.model("User",userSchema);
async function saveUser(a){if(validator.isEmail(a))try{await (new User({email:a})).save()}catch(b){console.error(b.message)}else console.error("Please provide a valid email")};